import Header from './jsx/Header'
import Footer from './jsx/Footer'
import Post from './jsx/Post'
import { useLoaderData } from 'react-router-dom'
import PageNotFound from './404'

import './css/general.css'
import { useState, useRef } from 'react'

//import { Editor } from "react-draft-wysiwyg"
import "react-draft-wysiwyg/dist/react-draft-wysiwyg.css"
import PostEditor from './jsx/PostEditor'

const ViewPage = () => {
    let post = useLoaderData()
    let localPost = Object.assign({},post)
    localPost.text = 'test'
    let mode = Boolean(false)
    let postReference = useRef(post)

    if(post != null){
        return (<>
            <Header/>
            <div class='main-body'>
                {
                    (mode)?
                        <>
                        <div class='editor-bar'>
                            <div onClick={() => mode = false}>
                                save
                            </div>
                        </div>
                        <PostEditor postReference = {postReference}/>
                        </>
                    :
                    <>
                        <div class='editor-bar'>
                            <button class='editor-bar__button' onClick={() => mode = true}>
                                edit
                            </button>
                        </div>
                        <Post post = {post} /> 
                    </>
                }
            </div>
            <Footer/>
        </>);
    }else
    {
        return <PageNotFound/>
    }
    
}

export default ViewPage;
