import '../css/Post.css'
import TextEditor from './TextEditor';

export default function Post({post}){
  return (
  <div class='post'>
    <div class='post__head'>
      <h2 class='post__header'>Header: <input type="text" value={post.header}/></h2>
      <div>
        <p class='post__author'>by {post.author}</p>
        <p class='post__date'>{post.date}</p>
      </div>
    </div>
    <TextEditor initialState={post.text} updateCallback={newVal => post.text = newVal}/>
    {/* <p class='post__text'>{post.text}</p> */}
    <img class='post__img' src={post.imageSrc} alt={post.imageAlt}/>
  </div>
  )
}
