import '../css/Post.css'
import TextEditor from './jsx/TextEditor';

const Post = ({post, saveCallback}) => {
  <div class='post'>
    <div class='post__head'>
      <h2 class='post__header'>{post.header}</h2>
      <div>
        <p class='post__author'>by {post.author}</p>
        <p class='post__date'>{post.date}</p>
      </div>
    </div>

    <TextEditor initialState={post.text} outputState={saveCallback}/>
    {/* <p class='post__text'>{post.text}</p> */}
    <img class='post__img' src={post.imageSrc} alt={post.imageAlt}/>
  </div>
};

export default Post;
