import React from 'react';
import ReactDOM from 'react-dom/client';
import {
  createBrowserRouter,
  createRoutesFromElements,
  Route,
  RouterProvider,
} from 'react-router-dom';

import './css/index.css';

import reportWebVitals from './reportWebVitals';

import Main from './pages/Main';
import View from './pages/View';
import PageNotFound from './pages/404';
import CreatePage from './pages/Create';

let postArray = {} 

const getAllPosts = async () => {
  const response = await fetch(`127.0.0.1:1001/posts`,{
    method: "GET"
  })
  let responseJson = await response.json()
  return responseJson
}

const router = createBrowserRouter(
  createRoutesFromElements((
    <>
    <Route path="/" 
    element={<Main postArray={getAllPosts()} />} />
    <Route 
      path=":id" 
      loader={({params})=>{

        let found
        fetch(`127.0.0.1:1001/posts/${params.id}`)
          .then(response=>found = response.json())

        return found
      }}
      element={<View/>}
    />
    <Route
      path="create"
      element={<CreatePage creationCallback={(newPost)=>{
        let id = postArray.length
        newPost.id = id
        postArray.push(Object.assign({},newPost))
        return id
      }}/>}
    />
    </>
  ))
);

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  // <React.StrictMode>
    <RouterProvider router={router} />
  // </React.StrictMode> */
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
